export default {
    transform_vCPU_API: () => {
			let inst_prefix_data = [gex_aws_instance_prefix.data];
			const $transformed_data = [];
			var response = inst_prefix_data.map((instance_item) => (
        Object.keys(instance_item).map((i) => (
            $transformed_data.push(
							{ 
								"x": instance_item[`${i}`].instance_type,
								"y": instance_item[`${i}`].vcpu
							}
						)
        ))
    ));

			return $transformed_data;
		},
		transform_RAM_API: () => {
				let inst_prefix_data = [gex_aws_instance_prefix.data];
				const $transformed_data = [];
				var response = inst_prefix_data.map((instance_item) => (
					Object.keys(instance_item).map((i) => (
							$transformed_data.push(
								{ 
									"x": instance_item[`${i}`].instance_type,
									"y": instance_item[`${i}`].memory
								}
							)
					))
			));

				return $transformed_data;
			},
			transform_Price_MIN_API: () => {
				let inst_prefix_data = [gex_aws_instance_prefix.data];
				const $transformed_data = [];
				var response = inst_prefix_data.map((instance_item) => (
					Object.keys(instance_item).map((i) => (
							$transformed_data.push(
								{ 
									"x": instance_item[`${i}`].instance_type,
									"y": instance_item[`${i}`].prices['us-east-1'].spot_min
								}
							)
					))
			));

				return $transformed_data;
			},
			transform_Price_MAX_API: () => {
				let inst_prefix_data = [gex_aws_instance_prefix.data];
				const $transformed_data = [];
				var response = inst_prefix_data.map((instance_item) => (
					Object.keys(instance_item).map((i) => (
						spot_max_currency_input.value >= instance_item[`${i}`].prices['us-east-1'].spot_max ?
							$transformed_data.push(
								{ 
									"x": instance_item[`${i}`].instance_type,
									"y": instance_item[`${i}`].prices['us-east-1'].spot_max
								}
							)
						: null
					))
			));

				return $transformed_data;
			},
}
