export default {
    transform_vCPU_API: () => {
			let inst_prefix_data = [gex_aws_instance_prefix.data];
			const $transformed_data = [];
			var response = inst_prefix_data.map((instance_item) => (
        Object.keys(instance_item).map((i) => (
					instance_constraints_dd_multi.selectedOptionValues.indexOf(instance_item[`${i}`].instance_type) ?
            $transformed_data.push(
							{ 
								"x": instance_item[`${i}`].instance_type,
								"y": instance_item[`${i}`].vcpu
							}
						)
					: null
        ))
    ));

			return $transformed_data;
		},
		transform_RAM_API: () => {
				let inst_prefix_data = [gex_aws_instance_prefix.data];
				const $transformed_data = [];
				var response = inst_prefix_data.map((instance_item) => (
					Object.keys(instance_item).map((i) => (
						instance_constraints_dd_multi.selectedOptionValues.indexOf(instance_item[`${i}`].instance_type) ?
							$transformed_data.push(
								{ 
									"x": instance_item[`${i}`].instance_type,
									"y": instance_item[`${i}`].memory
								}
							)
						: null
					))
			));

				return $transformed_data;
			},
			transform_Price_MIN_API: () => {
				let inst_prefix_data = [gex_aws_instance_prefix.data];
				const $transformed_data = [];
				var response = inst_prefix_data.map((instance_item) => (
					Object.keys(instance_item).map((i) => (
						instance_constraints_dd_multi.selectedOptionValues.indexOf(instance_item[`${i}`].instance_type) ?
							$transformed_data.push(
								{ 
									"x": instance_item[`${i}`].instance_type,
									"y": instance_item[`${i}`].prices['us-east-1'].spot_min.toFixed(4)
								}
							)
						: null
					))
			));

				return $transformed_data;
			},
			transform_Price_MAX_API: () => {
				let inst_prefix_data = [gex_aws_instance_prefix.data];
				const $transformed_data = [];
				var response = inst_prefix_data.map((instance_item) => (
					Object.keys(instance_item).map((i) => (
						//spot_max_currency_input.value >= instance_item[`${i}`].prices['us-east-1'].spot_max ?
						instance_constraints_dd_multi.selectedOptionValues.indexOf(instance_item[`${i}`].instance_type) ?
							$transformed_data.push(
								{ 
									"x": instance_item[`${i}`].instance_type,
									"y": instance_item[`${i}`].prices['us-east-1'].spot_max.toFixed(4)
								}
							)
						: null
						//: null
					))
			));

				return $transformed_data;
			},
			transform_Interruption_Rate_API: () => {
				let inst_prefix_data = [gex_aws_instance_prefix.data];
				const $transformed_data = [];
				var response = inst_prefix_data.map((instance_item) => (
					Object.keys(instance_item).map((i) => (
						//spot_max_currency_input.value >= instance_item[`${i}`].prices['us-east-1'].spot_max ?
						instance_item[`${i}`].instance_type.includes(instance_constraints_dd_multi.selectedOptionValues[0]) ?
							$transformed_data.push(
								{ 
									"x": instance_item[`${i}`].instance_type,
									"y": instance_item[`${i}`].interruption_rate['us-east-1']
								}
							)
						: null
						//: null
					))
			));

				return $transformed_data;
			},
			my_test: () => {
				let inst_prefix_data = [gex_aws_instance_prefix.data];
				const $initial_data = [];
				const $transformed_data = [];

				const $instance_prefixes = instance_prefixed_dd_multi.selectedOptionValues;
  			const $instance_constraints = instance_constraints_dd_multi.selectedOptionValues;

				inst_prefix_data.map((instance_item) => (
					Object.keys(instance_item).map((i) => (
							$initial_data.push(instance_item[`${i}`])
					))
				))

				for(let i in $initial_data) {
					for (let j in $instance_prefixes) {
						for (let k in $instance_constraints) {
							if ($initial_data[i].instance_type === ($instance_prefixes[j] + '.' + $instance_constraints[k])) {
								$transformed_data.push($initial_data[i])
							}
						}
					}
				}

				return $transformed_data;
			}
}
